ee:variables.ee:set-variable:
output application/json var dynamicHost="http."++ lower(vars.fileDetails.subsidiary replace "-" with "") ++ ".sapi.host" var dynamicPort="http."++ lower(vars.fileDetails.subsidiary replace "-" with "") ++ ".sapi.port" var dynamicPath= "http."++ lower(vars.fileDetails.subsidiary replace "-" with "") ++ ".sapi.basepath" var dynamicClientId="http."++ lower(vars.fileDetails.subsidiary replace "-" with "") ++ ".sapi.client_id" var dynamicClientSecret= "http."++ lower(vars.fileDetails.subsidiary replace "-" with "") ++ ".sapi.client_secret"
---
if( Mule::p('routing.systems.sftp') contains vars.fileDetails.externalSystem){ host: Mule::p(dynamicHost), port: Mule::p(dynamicPort), path: Mule::p(dynamicPath), client_id: Mule::p(dynamicClientId), client_secret: Mule::p(dynamicClientSecret)
}
else if( Mule::p('routing.systems.hulft') contains vars.fileDetails.externalSystem){ host: Mule::p('http.hulft.sapi.host'), port: Mule::p('http.hulft.sapi.port'), path: Mule::p('http.hulft.sapi.basepath') } else { }
